    /**
     * 获取弹幕
     * @param videoUrl 视频链接
     * @return data
     */
    public List<JSONObject> getBarrage(String videoUrl, String cookie) throws ScriptException, NoSuchMethodException, IOException {
        List<JSONObject> jsonList = new ArrayList<>();
        log.info("cookie is : {}", cookie);
        this.cookie = cookie;
        this.videoUrl = videoUrl;
        String html = okHttpClient.newCall(getRequest(videoUrl)).execute().body().string();
        try {
            js.eval(ykJS);
        } catch (ScriptException e) {
            log.error("JS 加载失败 {}", e);
            e.printStackTrace();
        }
        invocable = (Invocable) js;
        int mat = getMat(html);
        log.info("VIDEO LENGTH IS : {} MINUTES", mat);
        for (int i = 0; i <= mat; i++) {
            LinkedHashMap<String, Object> formParam = paramExtraction(i);
            formParam.put("mat", i);
            String formStr = new JSONObject(formParam).toJSONString();
            log.info("form data is : {}", formStr);
            long t = new Date().getTime();
            String e = token + "&" + t + "&" + appKey + "&" + formStr;
            log.info("before request sign : {}", e);
            String requestSign = DigestUtils.md5Hex(e);
            log.info("request sign : {}", requestSign);
            String barrageUrl = " https://acs.youku.com/h5/mopen.youku.danmu.list/1.0/?jsv=2.5.1&appKey=24679788&t=" + t + "&sign=" + requestSign + "&api=mopen.youku.danmu.list&v=1.0&type=originaljson&dataType=jsonp&timeout=20000&jsonpIncPrefix=utility";
            jsonList.add(barrageRequest(barrageUrl, formStr));
        }

        return jsonList;
    }

    /**
     * 参数提取
     * @param html 网页内容
     * @return null
     */
    private LinkedHashMap<String, Object> paramExtraction(int mat){
        String vid = getDataByRe(videoUrl, "(?<=id_).*?(?=\\.)");
        token = getDataByRe(cookie, "(?<=_m_h5_tk=).*?(?=_\\d+)");
//        log.info("token is : {}", token);
        String guid = "";
        if (cookie != null){
            guid = getDataByRe(cookie, "(?<=cna=).*?(?=;)");
        }

        long time = new Date().getTime();
        LinkedHashMap<String, Object> formData = new LinkedHashMap<>();
        LinkedHashMap<String, Object> msgLinkedHashMap = new LinkedHashMap<>();
        msgLinkedHashMap.put("ctime", time);
        msgLinkedHashMap.put("ctype", 10004);
        msgLinkedHashMap.put("cver", "v1.0");
        msgLinkedHashMap.put("guid", guid);
        msgLinkedHashMap.put("mat", mat);
        msgLinkedHashMap.put("mcount", 1);
        msgLinkedHashMap.put("pid", 0);
        msgLinkedHashMap.put("sver", "3.1.0");
        msgLinkedHashMap.put("type", 1);
        msgLinkedHashMap.put("vid", vid);
        String msg = Base64.getEncoder().encodeToString(new JSONObject(msgLinkedHashMap).toJSONString().trim().getBytes());

        formData.put("pid", 0);
        formData.put("ctype", 10004);
        formData.put("sver", "3.1.0");
        formData.put("cver", "v1.0");
        formData.put("ctime", time);
        formData.put("guid", guid);
        formData.put("vid", vid);
        formData.put("mat", mat);
        formData.put("mcount", 1);
        formData.put("type", 1);
        formData.put("msg", msg);
        formData.put("sign", DigestUtils.md5Hex(msg.concat(publicKey)));
        return formData;
    }

    /**
     * 获取时长
     */
    private int getMat(String html) throws ScriptException, NoSuchMethodException {
        String dataByRe = getDataByRe(html, "(?<=seconds: ').*?(?=',)");
            if (dataByRe != null){
                String timeFormat = (String)invocable.invokeFunction("timeFormat", dataByRe);
                String[] split = timeFormat.split(":");
                int hour,minutes;
                if (split.length == 3){
                    hour = Integer.parseInt(split[0]);
                    minutes = Integer.parseInt(split[1]);
                    return hour * 60 + minutes + 1;
                }
                if (split.length == 2){
                    minutes = Integer.parseInt(split[0]);
                    return minutes + 1;
                }
                return 0;
            }
            throw new NullPointerException("获取播放时长失败..");
    }

    public String getDataByRe(String text, String re){
        Matcher matcher = Pattern.compile(re).matcher(text);
        while (matcher.find()){
            return matcher.group();
        }
        return "";
    }

