    private JSONObject barrageRequest(String url, String data) throws UnsupportedEncodingException {
        MediaType mediaType = MediaType.parse("application/x-www-form-urlencoded");
        RequestBody body = RequestBody.create(mediaType, "data=" + URLEncoder.encode(data, "utf-8"));
        Request request = new Request.Builder().url(url).method("POST", body).build();
        request = getHeaderRequest(request, cookie);
        try{
            Response response = okHttpClient.newCall(request).execute();
            String result = response.body().string();
            log.info("final barrage is : {}", result);
            return JSONObject.parseObject(result).getJSONObject("data");
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null;
    }

